      out.write("						}\r\n");
      out.write("						liListNode.innerHTML=liText;\r\n");
      out.write("						ulListNode.appendChild(liListNode);\r\n");
      out.write("					}\r\n");
      out.write("					if(z>3&&z==(lists.length-1)){\r\n");
